module.exports = function getFilenameAndContents(args){
  const help = args.help ? true : false // defaults to undefined otherwise
  let filename = 'index.html'
  let title = 'Title of Page'

  if(args._.length > 0){
    filename = args._[0]
  }

  if(args.title != undefined){
    title = args.title
  }

  const contents = 
`<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">

    <!--for scaling across various devices-->
    <meta name="viewport" content="width=device-width, initial-scale=1.0"> 

    <!--set the color for the URL bar on Chrome on Android phones/tablets-->
    <meta name="theme-color" content="#000000">

    <title>${title}</title> 

    <!--stylesheet--> 
    <link rel="stylesheet" type="text/css" href="styles.css">

    <!--javascript-->
    <script type="text/javascript" src="main.js"></script>
</head>
<body>
    <h1>Hello World</h1>
</body>
</html>` 

  return {filename, contents, help}
}